/* eslint-disable no-unused-vars */
import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import { motion, AnimatePresence } from "framer-motion";
import {
  Search,
  Bell,
  Settings,
  User,
  LogOut,
  Menu,
  Sun,
  Moon,
  ChevronDown,
  Mail,
  Shield,
  Zap,
  Clock,
  CheckCircle,
  X,
} from "lucide-react";

import { useAuth } from "../../contexts/AuthContext";
import { useEmail } from "../../contexts/EmailContext";
import { useSettings } from "../../contexts/SettingsContext";

const HeaderContainer = styled.header`
  height: ${({ theme }) => theme.layout.header.height};
  background: ${({ theme }) => theme.colors.surface};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 ${({ theme }) => theme.spacing[6]};
  position: sticky;
  top: 0;
  z-index: ${({ theme }) => theme.zIndex.sticky};

  @media (max-width: 768px) {
    padding: 0 ${({ theme }) => theme.spacing[4]};
  }
`;

const LeftSection = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[4]};
  flex: 1;
`;

const MenuButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border: none;
  background: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  svg {
    width: 20px;
    height: 20px;
  }
`;

const SearchContainer = styled.div`
  position: relative;
  flex: 1;
  max-width: 500px;

  @media (max-width: 768px) {
    max-width: 200px;
  }

  @media (max-width: 480px) {
    display: none;
  }
`;

const SearchInput = styled.input`
  width: 100%;
  padding: ${({ theme }) => theme.spacing[2]} ${({ theme }) => theme.spacing[4]};
  padding-left: ${({ theme }) => theme.spacing[10]};
  padding-right: ${({ theme }) => theme.spacing[10]};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  background: ${({ theme }) => theme.colors.background};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  transition: all ${({ theme }) => theme.transitions.fast};

  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.colors.primary};
    box-shadow: 0 0 0 3px ${({ theme }) => theme.colors.primary}20;
  }

  &::placeholder {
    color: ${({ theme }) => theme.colors.textTertiary};
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: ${({ theme }) => theme.spacing[3]};
  top: 50%;
  transform: translateY(-50%);
  color: ${({ theme }) => theme.colors.textTertiary};
  display: flex;
  align-items: center;

  svg {
    width: 16px;
    height: 16px;
  }
`;

const SearchClear = styled.button`
  position: absolute;
  right: ${({ theme }) => theme.spacing[3]};
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: ${({ theme }) => theme.colors.textTertiary};
  cursor: pointer;
  padding: 2px;
  border-radius: ${({ theme }) => theme.borderRadius.sm};
  display: flex;
  align-items: center;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  svg {
    width: 14px;
    height: 14px;
  }
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
`;

const SyncStatus = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  padding: ${({ theme }) => theme.spacing[2]} ${({ theme }) => theme.spacing[3]};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: ${({ theme }) => theme.typography.fontSize.xs};
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  background: ${({ theme, syncing }) =>
    syncing ? theme.colors.warning + "15" : theme.colors.success + "15"};
  color: ${({ theme, syncing }) =>
    syncing ? theme.colors.warning : theme.colors.success};

  svg {
    width: 14px;
    height: 14px;
    animation: ${({ syncing }) =>
      syncing ? "spin 1s linear infinite" : "none"};
  }

  @media (max-width: 640px) {
    display: none;
  }
`;

const IconButton = styled.button`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border: none;
  background: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  &.active {
    background: ${({ theme }) => theme.colors.primary + "15"};
    color: ${({ theme }) => theme.colors.primary};
  }

  svg {
    width: 20px;
    height: 20px;
  }
`;

const NotificationBadge = styled.div`
  position: absolute;
  top: 4px;
  right: 4px;
  width: 18px;
  height: 18px;
  background: ${({ theme }) => theme.colors.error};
  color: ${({ theme }) => theme.colors.white};
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: ${({ theme }) => theme.typography.fontSize.xs};
  font-weight: ${({ theme }) => theme.typography.fontWeight.bold};
  border: 2px solid ${({ theme }) => theme.colors.surface};
`;

const UserMenuButton = styled.button`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  padding: ${({ theme }) => theme.spacing[2]};
  border: none;
  background: none;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
  }

  &.active {
    background: ${({ theme }) => theme.colors.primary + "15"};
  }
`;

const UserAvatar = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background: linear-gradient(
    135deg,
    ${({ theme }) => theme.colors.primary},
    ${({ theme }) => theme.colors.secondary}
  );
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
`;

const UserInfo = styled.div`
  text-align: left;
  display: none;

  @media (min-width: 640px) {
    display: block;
  }

  .user-name {
    font-size: ${({ theme }) => theme.typography.fontSize.sm};
    font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
    color: ${({ theme }) => theme.colors.textPrimary};
    line-height: 1.2;
  }

  .user-role {
    font-size: ${({ theme }) => theme.typography.fontSize.xs};
    color: ${({ theme }) => theme.colors.textSecondary};
    line-height: 1.2;
  }
`;

const DropdownContainer = styled.div`
  position: relative;
`;

const DropdownMenu = styled(motion.div)`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: ${({ theme }) => theme.spacing[2]};
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.boxShadow.xl};
  z-index: ${({ theme }) => theme.zIndex.dropdown};
  min-width: ${({ width }) => width || "280px"};
  max-height: 400px;
  overflow-y: auto;
`;

const DropdownHeader = styled.div`
  padding: ${({ theme }) => theme.spacing[4]};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
  color: ${({ theme }) => theme.colors.textPrimary};
`;

const DropdownItem = styled.button`
  width: 100%;
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  border: none;
  background: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};
  text-align: left;

  &:hover {
    background: ${({ theme }) => theme.colors.gray50};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  &.danger {
    color: ${({ theme }) => theme.colors.error};

    &:hover {
      background: ${({ theme }) => theme.colors.error + "10"};
    }
  }

  svg {
    width: 16px;
    height: 16px;
  }
`;

const NotificationItem = styled.div`
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  cursor: pointer;
  transition: background-color ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray50};
  }

  &:last-child {
    border-bottom: none;
  }

  &.unread {
    background: ${({ theme }) => theme.colors.primary + "05"};
    border-left: 3px solid ${({ theme }) => theme.colors.primary};
  }

  .notification-header {
    display: flex;
    align-items: center;
    gap: ${({ theme }) => theme.spacing[2]};
    margin-bottom: ${({ theme }) => theme.spacing[1]};

    .notification-icon {
      width: 16px;
      height: 16px;
      color: ${({ theme }) => theme.colors.primary};
    }

    .notification-title {
      font-size: ${({ theme }) => theme.typography.fontSize.sm};
      font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
      color: ${({ theme }) => theme.colors.textPrimary};
      flex: 1;
    }

    .notification-time {
      font-size: ${({ theme }) => theme.typography.fontSize.xs};
      color: ${({ theme }) => theme.colors.textTertiary};
    }
  }

  .notification-content {
    font-size: ${({ theme }) => theme.typography.fontSize.sm};
    color: ${({ theme }) => theme.colors.textSecondary};
    line-height: 1.4;
  }
`;

const Header = ({ onMenuToggle, showMenuButton = false }) => {
  const { user, logout } = useAuth();
  const { setSearch, syncing } = useEmail();
  const { theme, toggleTheme } = useSettings();
  const [searchValue, setSearchValue] = useState("");
  const [showNotifications, setShowNotifications] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);
  const notificationRef = useRef(null);
  const userMenuRef = useRef(null);

  // Mock notifications - in real app, this would come from context/API
  const notifications = [
    {
      id: 1,
      title: "New spam emails detected",
      content: "15 emails have been automatically moved to spam folder",
      time: "2 min ago",
      icon: Shield,
      unread: true,
    },
    {
      id: 2,
      title: "AI model retrained",
      content: "Classification accuracy improved by 2.3%",
      time: "1 hour ago",
      icon: Zap,
      unread: true,
    },
    {
      id: 3,
      title: "Email sync completed",
      content: "42 new emails synchronized from Gmail",
      time: "3 hours ago",
      icon: CheckCircle,
      unread: false,
    },
  ];

  const unreadCount = notifications.filter((n) => n.unread).length;

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        notificationRef.current &&
        !notificationRef.current.contains(event.target)
      ) {
        setShowNotifications(false);
      }
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setShowUserMenu(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Handle search
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchValue(value);
    setSearch(value);
  };

  const handleSearchClear = () => {
    setSearchValue("");
    setSearch("");
  };

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
  };

  const getInitials = (firstName, lastName) => {
    return `${firstName?.[0] || ""}${lastName?.[0] || ""}`.toUpperCase() || "U";
  };

  const formatTime = (timeString) => {
    // Simple time formatting - in real app, use proper date library
    return timeString;
  };

  return (
    <HeaderContainer>
      <LeftSection>
        {showMenuButton && (
          <MenuButton onClick={onMenuToggle}>
            <Menu />
          </MenuButton>
        )}

        <SearchContainer>
          <SearchIcon>
            <Search />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search emails..."
            value={searchValue}
            onChange={handleSearchChange}
          />
          {searchValue && (
            <SearchClear onClick={handleSearchClear}>
              <X />
            </SearchClear>
          )}
        </SearchContainer>
      </LeftSection>

      <RightSection>
        <SyncStatus syncing={syncing}>
          {syncing ? <Clock /> : <CheckCircle />}
          {syncing ? "Syncing..." : "Synced"}
        </SyncStatus>

        <IconButton onClick={toggleTheme}>
          {theme === "dark" ? <Sun /> : <Moon />}
        </IconButton>

        <DropdownContainer ref={notificationRef}>
          <IconButton
            onClick={() => setShowNotifications(!showNotifications)}
            className={showNotifications ? "active" : ""}
          >
            <Bell />
            {unreadCount > 0 && (
              <NotificationBadge>
                {unreadCount > 9 ? "9+" : unreadCount}
              </NotificationBadge>
            )}
          </IconButton>

          <AnimatePresence>
            {showNotifications && (
              <DropdownMenu
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
                width="320px"
              >
                <DropdownHeader>
                  Notifications
                  {unreadCount > 0 && ` (${unreadCount} new)`}
                </DropdownHeader>

                {notifications.length > 0 ? (
                  notifications.map((notification) => {
                    const IconComponent = notification.icon;
                    return (
                      <NotificationItem
                        key={notification.id}
                        className={notification.unread ? "unread" : ""}
                      >
                        <div className="notification-header">
                          <IconComponent className="notification-icon" />
                          <span className="notification-title">
                            {notification.title}
                          </span>
                          <span className="notification-time">
                            {formatTime(notification.time)}
                          </span>
                        </div>
                        <div className="notification-content">
                          {notification.content}
                        </div>
                      </NotificationItem>
                    );
                  })
                ) : (
                  <NotificationItem>
                    <div className="notification-content">No notifications</div>
                  </NotificationItem>
                )}
              </DropdownMenu>
            )}
          </AnimatePresence>
        </DropdownContainer>

        <DropdownContainer ref={userMenuRef}>
          <UserMenuButton
            onClick={() => setShowUserMenu(!showUserMenu)}
            className={showUserMenu ? "active" : ""}
          >
            <UserAvatar>
              {getInitials(user?.firstName, user?.lastName)}
            </UserAvatar>
            <UserInfo>
              <div className="user-name">
                {user?.firstName} {user?.lastName}
              </div>
              <div className="user-role">User</div>
            </UserInfo>
            <ChevronDown size={16} />
          </UserMenuButton>

          <AnimatePresence>
            {showUserMenu && (
              <DropdownMenu
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
                width="200px"
              >
                <DropdownItem onClick={() => setShowUserMenu(false)}>
                  <User />
                  Profile
                </DropdownItem>

                <DropdownItem onClick={() => setShowUserMenu(false)}>
                  <Settings />
                  Settings
                </DropdownItem>

                <DropdownItem onClick={handleLogout} className="danger">
                  <LogOut />
                  Sign Out
                </DropdownItem>
              </DropdownMenu>
            )}
          </AnimatePresence>
        </DropdownContainer>
      </RightSection>
    </HeaderContainer>
  );
};

export default Header;
