import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Routes, Route, useNavigate, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";
import { toast } from "react-hot-toast";

import { useAuth } from "../contexts/AuthContext";
import { useSettings } from "../contexts/SettingsContext";

import Sidebar from "../components/layout/Sidebar";
import Header from "../components/layout/Header";
import LoadingSpinner from "../components/common/LoadingSpinner";
import EmailList from "./EmailList";
import Analytics from "./Analytics";
import Settings from "./Settings";
import DashboardHome from "../components/dashboard/DashboardHome";

const DashboardContainer = styled.div`
  display: flex;
  min-height: 100vh;
  background: ${({ theme }) => theme.colors.background};
`;

const SidebarContainer = styled(motion.div)`
  width: ${({ theme, collapsed }) =>
    collapsed
      ? theme.layout.sidebar.collapsedWidth
      : theme.layout.sidebar.width};
  flex-shrink: 0;
  background: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid ${({ theme }) => theme.colors.border};
  transition: width ${({ theme }) => theme.transitions.normal};
  z-index: ${({ theme }) => theme.zIndex.docked};
  @media (max-width: 768px) {
    position: fixed;
    top: 0;
    left: ${({ collapsed }) => (collapsed ? "-280px" : "0")};
    height: 100vh;
    width: 280px;
    box-shadow: ${({ theme, collapsed }) =>
      collapsed ? "none" : theme.boxShadow.xl};
    z-index: ${({ theme }) => theme.zIndex.overlay};
  }
`;

const MainContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const HeaderContainer = styled.div`
  height: ${({ theme }) => theme.layout.header.height};
  background: ${({ theme }) => theme.colors.surface};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  z-index: ${({ theme }) => theme.zIndex.sticky};
  position: sticky;
  top: 0;
`;

const ContentContainer = styled.main`
  flex: 1;
  padding: ${({ theme }) => theme.spacing[6]};
  max-width: ${({ theme }) => theme.layout.content.maxWidth};
  margin: 0 auto;
  width: 100%;
  @media (max-width: 768px) {
    padding: ${({ theme }) => theme.spacing[4]};
  }
`;

const MobileOverlay = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: ${({ theme }) => theme.zIndex.overlay - 1};
  @media (max-width: 768px) {
    display: block;
  }
`;

const Dashboard = () => {
  const { logout } = useAuth();
  const { sidebarCollapsed, toggleSidebar } = useSettings();
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [hasConnectedAccount, setHasConnectedAccount] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    if (queryParams.get("connected") === "true") {
      navigate("/emails", { replace: true });
    }

    const fetchDashboardData = async () => {
      setLoading(true);
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const response = await axios.get("/api/emails/analyze", config);

        if (response.data && response.data.length > 0) {
          setHasConnectedAccount(true);
          setEmails(response.data);
        } else {
          setHasConnectedAccount(false);
        }
      } catch (error) {
        setHasConnectedAccount(false);
        if (error.response && error.response.status === 401) {
          toast.error("Your session has expired. Please log in again.");
          logout();
          navigate("/login");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate, logout, location.search]);

  const handleMobileMenuToggle = () => {
    if (isMobile) {
      setShowMobileMenu(!showMobileMenu);
    } else {
      toggleSidebar();
    }
  };

  if (loading) {
    return <LoadingSpinner fullHeight text="Loading your dashboard..." />;
  }

  return (
    <DashboardContainer>
      <AnimatePresence>
        {showMobileMenu && isMobile && (
          <MobileOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowMobileMenu(false)}
          />
        )}
      </AnimatePresence>

      <SidebarContainer
        collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
      >
        <Sidebar
          collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
          onToggle={handleMobileMenuToggle}
          isMobile={isMobile}
        />
      </SidebarContainer>

      <MainContainer>
        <HeaderContainer>
          <Header onMenuToggle={handleMobileMenuToggle} showMenuButton={true} />
        </HeaderContainer>

        <ContentContainer>
          {!hasConnectedAccount ? (
            <DashboardHome />
          ) : (
            <Routes>
              <Route path="/" element={<EmailList emails={emails} />} />
              <Route path="/emails" element={<EmailList emails={emails} />} />
              <Route path="/analytics" element={<Analytics />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          )}
        </ContentContainer>
      </MainContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
