import React from "react";
import styled from "styled-components";
import {
  Menu,
  Search,
  User as UserIcon,
  Sun,
  Moon,
  LogOut,
} from "lucide-react";
import { useSettings } from "../../contexts/SettingsContext";
import { useAuth } from "../../contexts/AuthContext";
import Dropdown from "../common/Dropdown";

const Header = ({ user, onMenuToggle, searchQuery, onSearchChange }) => {
  const { theme, toggleTheme } = useSettings();
  const { logout } = useAuth();

  const handleDropdownChange = (value) => {
    if (value === "logout") {
      logout();
    }
  };

  return (
    <HeaderWrapper>
      <LeftSection>
        <MenuButton onClick={onMenuToggle}>
          <Menu size={24} />
        </MenuButton>
        <SearchWrapper>
          <SearchIcon size={20} />
          <SearchInput
            type="text"
            placeholder="Search mail"
            value={searchQuery}
            onChange={(e) => onSearchChange(e.target.value)}
          />
        </SearchWrapper>
      </LeftSection>
      <RightSection>
        <MenuButton onClick={toggleTheme}>
          {theme === "light" ? <Moon size={20} /> : <Sun size={20} />}
        </MenuButton>
        <Dropdown
          trigger={
            <ProfileSection>
              <UserIcon size={24} />
              <ProfileName>{user ? user.username : "User"}</ProfileName>
            </ProfileSection>
          }
          items={[
            { label: "Logout", value: "logout", icon: <LogOut size={16} /> },
          ]}
          onChange={handleDropdownChange}
        />
      </RightSection>
    </HeaderWrapper>
  );
};

export default Header;
