import React, { useState } from "react";
import styled, { css } from "styled-components";
import {
  Sun,
  Bot,
  Palette,
  Link2,
  Shield,
  Edit,
  Trash2,
  Circle,
} from "lucide-react";
import { useSettings } from "../contexts/SettingsContext";

// --- Main Layout Components ---

const SettingsLayout = styled.div`
  display: flex;
  gap: ${({ theme }) => theme.spacing[8]};
  padding: ${({ theme }) => theme.spacing[2]};

  @media (max-width: 768px) {
    flex-direction: column;
    gap: ${({ theme }) => theme.spacing[4]};
  }
`;

const SettingsNav = styled.nav`
  display: flex;
  flex-direction: column;
  gap: ${({ theme }) => theme.spacing[2]};
  width: 240px;
  flex-shrink: 0;

  @media (max-width: 768px) {
    width: 100%;
    flex-direction: row;
    overflow-x: auto;
    -ms-overflow-style: none; /* IE and Edge */
    scrollbar-width: none; /* Firefox */
    &::-webkit-scrollbar {
      display: none; /* Chrome, Safari, and Opera */
    }
  }
`;

const NavItem = styled.button`
  /* Reset button styles */
  background: none;
  border: none;
  text-align: left;
  width: 100%;

  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  cursor: pointer;
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  color: ${({ theme, $active }) =>
    $active ? theme.colors.textPrimary : theme.colors.textSecondary};
  background: ${({ theme, $active }) =>
    $active ? `${theme.colors.primary}15` : "transparent"};
  transition: all ${({ theme }) => theme.transitions.fast};

  svg {
    width: 20px;
    height: 20px;
    color: ${({ theme, $active }) =>
      $active ? theme.colors.primary : theme.colors.textTertiary};
  }

  &:hover {
    background: ${({ theme }) => `${theme.colors.primary}10`};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  @media (max-width: 768px) {
    flex-shrink: 0;
  }
`;

const SettingsContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: ${({ theme }) => theme.spacing[6]};
`;

// --- Reusable UI Components ---

const Section = styled.div`
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
`;

const SectionHeader = styled.div`
  padding: ${({ theme }) => theme.spacing[4]} ${({ theme }) => theme.spacing[6]};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  h3 {
    font-size: ${({ theme }) => theme.typography.fontSize.lg};
    font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
    color: ${({ theme }) => theme.colors.textPrimary};
    margin: 0;
  }
  p {
    font-size: ${({ theme }) => theme.typography.fontSize.sm};
    color: ${({ theme }) => theme.colors.textSecondary};
    margin: ${({ theme }) => theme.spacing[1]} 0 0;
  }
`;

const SectionContent = styled.div`
  padding: ${({ theme }) => theme.spacing[6]};
  display: flex;
  flex-direction: column;
  gap: ${({ theme }) => theme.spacing[6]};
`;

const SectionFooter = styled.div`
  padding: ${({ theme }) => theme.spacing[4]} ${({ theme }) => theme.spacing[6]};
  border-top: 1px solid ${({ theme }) => theme.colors.border};
  background: ${({ theme }) => theme.colors.background};
  display: flex;
  justify-content: flex-end;
  border-bottom-left-radius: ${({ theme }) => theme.borderRadius.lg};
  border-bottom-right-radius: ${({ theme }) => theme.borderRadius.lg};
`;

const Button = styled.button`
  padding: ${({ theme }) => theme.spacing[2]} ${({ theme }) => theme.spacing[4]};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  transition: all ${({ theme }) => theme.transitions.fast};

  ${({ variant = "primary", theme }) =>
    variant === "primary" &&
    css`
      background: ${theme.colors.primary};
      color: ${theme.colors.white};
      border: 1px solid ${theme.colors.primary};
      &:hover {
        background: ${theme.colors.primaryHover};
      }
    `}

  ${({ variant = "secondary", theme }) =>
    variant === "secondary" &&
    css`
      background: ${theme.colors.surface};
      color: ${theme.colors.textPrimary};
      border: 1px solid ${theme.colors.border};
      &:hover {
        background: ${theme.colors.background};
      }
    `}
`;

const ListItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: ${({ theme }) => theme.spacing[3]} 0;
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  &:last-child {
    border-bottom: none;
    padding-bottom: 0;
  }
  &:first-child {
    padding-top: 0;
  }
`;

const ItemContent = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  font-weight: 500;
`;

const ItemActions = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  svg {
    cursor: pointer;
    color: ${({ theme }) => theme.colors.textTertiary};
    &:hover {
      color: ${({ theme }) => theme.colors.textPrimary};
    }
  }
`;

const ColorSwatch = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${({ color }) => color};
`;

const StatusIndicator = styled.div`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  color: ${({ theme }) => theme.colors.success};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: 500;

  svg {
    width: 12px;
    height: 12px;
  }
`;

const ToggleSwitch = styled.label`
  position: relative;
  display: inline-block;
  width: 44px;
  height: 24px;

  input {
    opacity: 0;
    width: 0;
    height: 0;
  }

  .slider {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: ${({ theme }) => theme.colors.gray400};
    transition: 0.4s;
    border-radius: 24px;
  }

  .slider:before {
    position: absolute;
    content: "";
    height: 18px;
    width: 18px;
    left: 3px;
    bottom: 3px;
    background-color: white;
    transition: 0.4s;
    border-radius: 50%;
  }

  input:checked + .slider {
    background-color: ${({ theme }) => theme.colors.primary};
  }

  input:checked + .slider:before {
    transform: translateX(20px);
  }
`;

// --- Sectional Components ---

const AIClassificationSettings = () => (
  <Section>
    <SectionHeader>
      <h3>AI & Classification</h3>
      <p>Manage how the AI categorizes and acts on your emails.</p>
    </SectionHeader>
    <SectionContent>
      <div>
        <h4>Category Management</h4>
        <ListItem>
          <ItemContent>
            <ColorSwatch color="#3b82f6" />
            <span>Invoice</span>
          </ItemContent>
          <ItemActions>
            <Edit size={18} /> <Trash2 size={18} />
          </ItemActions>
        </ListItem>
        <ListItem>
          <ItemContent>
            <ColorSwatch color="#10b981" />
            <span>Project Update</span>
          </ItemContent>
          <ItemActions>
            <Edit size={18} /> <Trash2 size={18} />
          </ItemActions>
        </ListItem>
        <Button variant="secondary" style={{ marginTop: "1rem" }}>
          Add New Category
        </Button>
      </div>
      <div>
        <h4>AI Sensitivity</h4>
        {/* Placeholder for a slider component */}
        <p>Strict (High Accuracy) -----o----- Flexible (More Suggestions)</p>
      </div>
      <div>
        <h4>Automated Actions</h4>
        {/* Placeholder for dropdowns */}
        <p>For "Invoice", apply action: [Move to folder: Finance]</p>
      </div>
      <div>
        <h4>Model Training</h4>
        <ListItem>
          <ItemContent>Enable AI feedback prompts</ItemContent>
          <ToggleSwitch>
            <input type="checkbox" defaultChecked />
            <span className="slider"></span>
          </ToggleSwitch>
        </ListItem>
        <Button variant="secondary">Reset Personalization Data</Button>
      </div>
    </SectionContent>
  </Section>
);

const ThemeOption = styled.button`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  padding: ${({ theme }) => theme.spacing[4]};
  border: 2px solid ${({ theme, $active }) => $active ? theme.colors.primary : theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  background: ${({ theme }) => theme.colors.background};
  cursor: pointer;
  width: 100px;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
    background: ${({ theme }) => theme.colors.surface};
  }

  .icon-placeholder {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: ${({ theme }) => theme.colors.border};
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

const InterfaceNotificationsSettings = () => {
  const { theme, setTheme } = useSettings();

  return (
    <Section>
      <SectionHeader>
        <h3>Interface & Notifications</h3>
        <p>Customize the look and feel, and manage notifications.</p>
      </SectionHeader>
      <SectionContent>
        <div>
          <h4>Appearance</h4>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <ThemeOption $active={theme === 'light'} onClick={() => setTheme('light')}>
              <div className="icon-placeholder"><Sun /></div>
              <span>Light</span>
            </ThemeOption>
            <ThemeOption $active={theme === 'dark'} onClick={() => setTheme('dark')}>
               <div className="icon-placeholder" style={{ background: '#333' }}><Sun color="white" /></div>
              <span>Dark</span>
            </ThemeOption>
          </div>
        </div>
        <div>
          <h4>Notification Preferences</h4>
          <ListItem>
            <ItemContent>Send daily summary digest</ItemContent>
            <input type="checkbox" />
          </ListItem>
          <ListItem>
            <ItemContent>Notify me for high-priority categories</ItemContent>
            <input type="checkbox" defaultChecked />
          </ListItem>
        </div>
      </SectionContent>
    </Section>
  );
};

const ConnectedAccountsSettings = () => (
  <Section>
    <SectionHeader>
      <h3>Connected Accounts</h3>
      <p>Manage the email accounts linked to this application.</p>
    </SectionHeader>
    <SectionContent>
      <ListItem>
        <ItemContent>user@gmail.com</ItemContent>
        <ItemActions>
          <StatusIndicator>
            <Circle fill="currentColor" /> Connected
          </StatusIndicator>
          <Button variant="secondary">Disconnect</Button>
        </ItemActions>
      </ListItem>
    </SectionContent>
    <SectionFooter>
      <Button variant="primary">Connect New Account</Button>
    </SectionFooter>
  </Section>
);

const SecuritySettings = () => (
  <Section>
    <SectionHeader>
      <h3>Security</h3>
      <p>Manage your account's security settings.</p>
    </SectionHeader>
    <SectionContent>
      <ListItem>
        <ItemContent>
          <div>
            <strong>Two-Factor Authentication (2FA)</strong>
            <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>Disabled</p>
          </div>
        </ItemContent>
        <Button variant="secondary">Setup</Button>
      </ListItem>
      <ListItem>
        <ItemContent>
          <strong>Active Sessions</strong>
        </ItemContent>
        <Button variant="secondary">Log out all other sessions</Button>
      </ListItem>
    </SectionContent>
  </Section>
);

// --- Main Settings Component ---

const TABS = {
  ai: "AI & Classification",
  interface: "Interface & Notifications",
  accounts: "Connected Accounts",
  security: "Security",
};

const Settings = () => {
  const [activeTab, setActiveTab] = useState("ai");

  const renderContent = () => {
    switch (activeTab) {
      case "ai":
        return <AIClassificationSettings />;
      case "interface":
        return <InterfaceNotificationsSettings />;
      case "accounts":
        return <ConnectedAccountsSettings />;
      case "security":
        return <SecuritySettings />;
      default:
        return <AIClassificationSettings />;
    }
  };

  return (
    <div>
      <h1 style={{ marginBottom: "2rem" }}>Settings</h1>
      <SettingsLayout>
        <SettingsNav>
          <NavItem $active={activeTab === "ai"} onClick={() => setActiveTab("ai")}>
            <Bot />
            <span>{TABS.ai}</span>
          </NavItem>
          <NavItem $active={activeTab === "interface"} onClick={() => setActiveTab("interface")}>
            <Palette />
            <span>{TABS.interface}</span>
          </NavItem>
          <NavItem $active={activeTab === "accounts"} onClick={() => setActiveTab("accounts")}>
            <Link2 />
            <span>{TABS.accounts}</span>
          </NavItem>
          <NavItem $active={activeTab === "security"} onClick={() => setActiveTab("security")}>
            <Shield />
            <span>{TABS.security}</span>
          </NavItem>
        </SettingsNav>
        <SettingsContent>{renderContent()}</SettingsContent>
      </SettingsLayout>
    </div>
  );
};

export default Settings;
