const EmailList = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("all");
  const {
    emails,
    categories,
    loading,
    error,
    fetchEmails,
    classifyEmail,
    syncEmailAccounts,
  } = useEmail();

  // Get category from URL or default to "inbox"
  const category = location.pathname.split("/").pop() || "inbox";

  useEffect(() => {
    fetchEmails({ category, search: searchTerm, filter });
  }, [category, searchTerm, filter, fetchEmails]);

  const handleEmailClick = (emailId) => {
    navigate(`/email/${emailId}`);
  };

  const handleSync = async () => {
    try {
      await syncEmailAccounts();
      toast.success("Emails synchronized successfully");
    } catch (error) {
      toast.error("Failed to sync emails");
    }
  };

  const handleClassify = async (emailId, event) => {
    event.stopPropagation();
    try {
      await classifyEmail(emailId);
      toast.success("Email classified successfully");
    } catch (error) {
      toast.error("Failed to classify email");
    }
  };

  const formatEmailTime = (date) => {
    if (isToday(date)) {
      return format(date, "h:mm a");
    }
    if (isYesterday(date)) {
      return "Yesterday";
    }
    if (isThisWeek(date)) {
      return format(date, "EEEE");
    }
    return format(date, "MMM d");
  };

  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  if (loading) {
    return (
      <LoadingContainer>
        <LoadingSpinner size="lg" text="Loading emails..." />
      </LoadingContainer>
    );
  }

  if (error) {
    return (
      <EmptyState
        icon={<AlertCircle />}
        title="Failed to load emails"
        description={error}
        action={{
          label: "Try Again",
          onClick: () => fetchEmails({ category, search: searchTerm, filter }),
        }}
      />
    );
  }

  if (!emails.length) {
    return (
      <EmptyState
        icon={<Inbox />}
        title="No emails found"
        description={
          searchTerm
            ? "Try adjusting your search or filters"
            : "Your email inbox is empty"
        }
        action={
          searchTerm && {
            label: "Clear Search",
            onClick: () => setSearchTerm(""),
          }
        }
      />
    );
  }

  return (
    <Container>
      <Header>
        <Title>
          <Inbox size={24} />
          {categories[category]?.label || "All Emails"}
          <Badge>{emails.length}</Badge>
        </Title>

        <SearchContainer>
          <SearchIcon>
            <Search />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Search emails..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>

        <ActionButtons>
          <Dropdown
            trigger={
              <IconButton>
                <Filter size={18} />
                <span>Filter</span>
                <ChevronDown size={16} />
              </IconButton>
            }
            items={[
              { label: "All", value: "all" },
              { label: "Unread", value: "unread" },
              { label: "Starred", value: "starred" },
              { label: "With Attachments", value: "attachments" },
            ]}
            value={filter}
            onChange={setFilter}
          />

          <IconButton onClick={handleSync}>
            <RefreshCw size={18} />
            <span>Sync</span>
          </IconButton>
        </ActionButtons>
      </Header>

      <EmailGrid>
        <AnimatePresence>
          {emails.map((email) => (
            <EmailCard
              key={email._id}
              unread={!email.isRead}
              onClick={() => handleEmailClick(email._id)}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.2 }}
            >
              <EmailIcon>{getInitials(email.from.name)}</EmailIcon>

              <EmailContent>
                <EmailHeader>
                  <EmailSender>{email.from.name}</EmailSender>
                  {email.category && (
                    <Badge color={categories[email.category]?.color}>
                      {categories[email.category]?.label}
                    </Badge>
                  )}
                </EmailHeader>
                <EmailSubject>{email.subject}</EmailSubject>
                <EmailPreview>{email.snippet}</EmailPreview>
              </EmailContent>

              <EmailMeta>
                <EmailTime>
                  {formatEmailTime(new Date(email.receivedAt))}
                </EmailTime>
                <div>
                  {email.isStarred && <Star size={16} />}
                  {email.hasAttachments && <Tag size={16} />}
                  <IconButton
                    onClick={(e) => handleClassify(email._id, e)}
                    title="Classify Email"
                  >
                    <CheckCircle size={16} />
                  </IconButton>
                </div>
              </EmailMeta>
            </EmailCard>
          ))}
        </AnimatePresence>
      </EmailGrid>
    </Container>
  );
};

