const Login = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [apiError, setApiError] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();
  const { isAuthenticated, login } = useAuth();

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
    control,
  } = useForm({
    mode: "onChange",
    defaultValues: {
      email: "",
      password: "",
    },
  });

  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || "/dashboard";
      navigate(from, { replace: true });
    }
  }, [isAuthenticated, navigate, location]);

  const formValues = useWatch({ control, name: ["email", "password"] });
  useEffect(() => {
    if (apiError) {
      setApiError(null);
    }
  }, [formValues, apiError]);

  const onSubmit = async (data) => {
    setIsLoading(true);
    setApiError(null);

    try {
      const res = await axios.post("/api/auth/login", data);
      login(res.data.token);
    } catch (err) {
      console.error("Login error:", err);
      if (err.response && err.response.data && err.response.data.msg) {
        setApiError(err.response.data.msg);
      } else {
        setApiError("An unexpected error occurred. Please try again.");
      }
    } finally {
      setIsLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword((prev) => !prev);
  };

  return (
    <LoginContainer>
      <LoginCard>
        <LogoSection>
          <Logo>Email Classifier</Logo>
          <Subtitle>AI-powered email management</Subtitle>
        </LogoSection>

        <Form onSubmit={handleSubmit(onSubmit)} noValidate>
          <FormGroup>
            <Label htmlFor="email">Email Address</Label>
            <InputWrapper>
              <InputIcon $error={!!errors.email}>
                <Mail />
              </InputIcon>
              <Input
                id="email"
                type="email"
                placeholder="Enter your email"
                $error={!!errors.email}
                {...register("email", {
                  required: "Email is required",
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: "Please enter a valid email address",
                  },
                })}
              />
            </InputWrapper>
            {errors.email && (
              <ErrorMessage>
                <AlertCircle />
                {errors.email.message}
              </ErrorMessage>
            )}
          </FormGroup>

          <FormGroup>
            <Label htmlFor="password">Password</Label>
            <InputWrapper>
              <InputIcon $error={!!errors.password}>
                <Lock />
              </InputIcon>
              <Input
                id="password"
                type={showPassword ? "text" : "password"}
                placeholder="Enter your password"
                $error={!!errors.password}
                $hasButton
                {...register("password", {
                  required: "Password is required",
                })}
              />
              <InputButton
                type="button"
                onClick={togglePasswordVisibility}
                aria-label={showPassword ? "Hide password" : "Show password"}
              >
                {showPassword ? <EyeOff /> : <Eye />}
              </InputButton>
            </InputWrapper>
            {errors.password && (
              <ErrorMessage>
                <AlertCircle />
                {errors.password.message}
              </ErrorMessage>
            )}
          </FormGroup>

          {apiError && (
            <ErrorMessage style={{ justifyContent: "center" }}>
              <AlertCircle />
              {apiError}
            </ErrorMessage>
          )}

          <SubmitButton type="submit" disabled={!isValid || isLoading}>
            {isLoading ? (
              <LoadingSpinner size="sm" color="white" />
            ) : (
              <>
                <LogIn />
                Sign In
              </>
            )}
          </SubmitButton>

          <ForgotPassword to="/forgot-password">
            Forgot your password?
          </ForgotPassword>
        </Form>

        <Divider>
          <span>or</span>
        </Divider>

        <SignupLink>
          Don't have an account? <Link to="/signup">Create one here</Link>
        </SignupLink>
      </LoginCard>
    </LoginContainer>
  );
};

export default Login;
