import React from "react";
import styled from "styled-components";
import { motion } from "framer-motion";
import { format } from "date-fns";
import { Inbox } from "lucide-react";
import EmptyState from "../components/common/EmptyState";

const EmailGrid = styled.div`
  display: flex;
  flex-direction: column;
  gap: ${({ theme }) => theme.spacing[2]};
`;

const EmailCard = styled(motion.div)`
  padding: ${({ theme }) => theme.spacing[4]};
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  display: grid;
  grid-template-columns: 1fr auto;
  gap: ${({ theme }) => theme.spacing[4]};
  align-items: center;
`;

const EmailContent = styled.div`
  min-width: 0;
`;

const EmailSender = styled.span`
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  color: ${({ theme }) => theme.colors.textPrimary};
`;

const EmailSubject = styled.div`
  color: ${({ theme }) => theme.colors.textPrimary};
  margin-bottom: ${({ theme }) => theme.spacing[1]};
`;

const EmailSnippet = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const EmailMeta = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: ${({ theme }) => theme.spacing[2]};
`;

const EmailDate = styled.div`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: ${({ theme }) => theme.typography.fontSize.xs};
  white-space: nowrap;
`;

const CategoryBadge = styled.span`
  padding: 0.25rem 0.75rem;
  border-radius: ${({ theme }) => theme.borderRadius.full};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  color: white;
  background-color: ${({ theme, category }) => {
    switch (category) {
      case "Promotions":
        return theme.colors.secondary;
      case "Updates":
        return theme.colors.info;
      case "Social":
        return theme.colors.success;
      default:
        return theme.colors.primary;
    }
  }};
`;

const EmailList = ({ emails }) => {
  if (!emails || emails.length === 0) {
    return (
      <EmptyState
        icon={<Inbox />}
        title="No Emails Yet"
        description="It looks like there are no emails in your inbox. Connect your account to start classifying."
      />
    );
  }

  return (
    <>
      <h1 style={{ marginBottom: "1rem" }}>Your Emails</h1>
      <EmailGrid>
        {emails.map((email) => (
          <EmailCard
            key={email.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.2 }}
          >
            <EmailContent>
              <EmailSender>{email.from}</EmailSender>
              <EmailSubject>{email.subject}</EmailSubject>
              <EmailSnippet>{email.snippet}</EmailSnippet>
            </EmailContent>
            <EmailMeta>
              <CategoryBadge category={email.category}>
                {email.category}
              </CategoryBadge>
              <EmailDate>
                {format(new Date(email.date), "MMM d, yyyy")}
              </EmailDate>
            </EmailMeta>
          </EmailCard>
        ))}
      </EmailGrid>
    </>
  );
};

export default EmailList;
