import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { useForm } from "react-hook-form";
import styled from "styled-components";
import { Eye, EyeOff, Mail, Lock, LogIn, AlertCircle } from "lucide-react";
import axios from "axios"; // Make sure to import axios
import { useAuth } from "../contexts/AuthContext"; // We still use this for isAuthenticated
import LoadingSpinner from "../components/common/LoadingSpinner"; // Assuming you have this component

// --- All your styled-components definitions go here ---
// (LoginContainer, LoginCard, etc. - no changes needed there)
// ... (I've omitted them for brevity, just keep your existing styles)
const LoginContainer = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(
    135deg,
    ${({ theme }) => theme.colors.primary}10 0%,
    ${({ theme }) => theme.colors.secondary}10 100%
  );
  padding: ${({ theme }) => theme.spacing[4]};
`;

const LoginCard = styled.div`
  background: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.xl};
  box-shadow: ${({ theme }) => theme.boxShadow.xl};
  padding: ${({ theme }) => theme.spacing[8]};
  width: 100%;
  max-width: 400px;
  border: 1px solid ${({ theme }) => theme.colors.border};
`;

const LogoSection = styled.div`
  text-align: center;
  margin-bottom: ${({ theme }) => theme.spacing[8]};
`;

