import React, { useState, useEffect } from "react";
import styled from "styled-components";
import api from "../styles/api";
import LoadingSpinner from "../components/common/LoadingSpinner";
import { BarChart2, AlertTriangle } from "lucide-react";

const AnalyticsContainer = styled.div`
  padding: ${({ theme }) => theme.spacing[4]};
`;

const Title = styled.h1`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  font-size: ${({ theme }) => theme.typography.fontSize["3xl"]};
  margin-bottom: ${({ theme }) => theme.spacing[6]};
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${({ theme }) => theme.spacing[6]};
`;

const StatCard = styled.div`
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  padding: ${({ theme }) => theme.spacing[6]};
  text-align: center;
`;

const StatValue = styled.p`
  font-size: ${({ theme }) => theme.typography.fontSize["4xl"]};
  font-weight: ${({ theme }) => theme.typography.fontWeight.bold};
  color: ${({ theme }) => theme.colors.primary};
  margin: 0;
`;

const StatLabel = styled.p`
  font-size: ${({ theme }) => theme.typography.fontSize.base};
  color: ${({ theme }) => theme.colors.textSecondary};
  margin-top: ${({ theme }) => theme.spacing[2]};
`;

const ErrorMessage = styled.div`
  background: ${({ theme }) => theme.colors.errorLight};
  color: ${({ theme }) => theme.colors.errorDark};
  border: 1px solid ${({ theme }) => theme.colors.error};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  padding: ${({ theme }) => theme.spacing[4]};
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
`;

const Analytics = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        setLoading(true);
        const response = await api.get("/auth/analytics");
        setData(response.data);
      } catch (err) {
        setError(err.response?.data?.error || "Failed to fetch analytics.");
      } finally {
        setLoading(false);
      }
    };

    fetchAnalytics();
  }, []);

  if (loading) {
    return <LoadingSpinner text="Fetching analytics..." />;
  }

  if (error) {
    return (
      <ErrorMessage>
        <AlertTriangle /> {error}
      </ErrorMessage>
    );
  }

  return (
    <AnalyticsContainer>
      <Title><BarChart2 size={32} /> Analytics</Title>
      {data && (
        <StatsGrid>
          <StatCard>
            <StatValue>{data.totalEmails}</StatValue>
            <StatLabel>Total Emails Scanned</StatLabel>
          </StatCard>
          {Object.entries(data.categoryCounts).map(([category, count]) => (
            <StatCard key={category}>
              <StatValue>{count}</StatValue>
              <StatLabel>{category}</StatLabel>
            </StatCard>
          ))}
        </StatsGrid>
      )}
    </AnalyticsContainer>
  );
};

export default Analytics;
