const Sidebar = ({ collapsed, onToggle, isMobile = false }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const isActive = (path) => {
    if (path === "/dashboard" || path === "/") {
      return location.pathname === "/dashboard" || location.pathname === "/";
    }
    return location.pathname.startsWith(path);
  };

  const getInitials = (username) => {
    if (!username) return "U";
    return username.substring(0, 2).toUpperCase();
  };

  return (
    <SidebarContainer collapsed={collapsed}>
      <SidebarHeader collapsed={collapsed}>
        <Logo to="/dashboard" collapsed={collapsed}>
          <div className="logo-icon">
            <Mail size={18} />
          </div>
          <span className="logo-text">Email Classifier</span>
        </Logo>

        {/* --- REMOVED: Desktop ToggleButton is now only in Header --- */}
        {isMobile && (
          <MobileCloseButton onClick={onToggle}>
            <X />
          </MobileCloseButton>
        )}
      </SidebarHeader>

      <SidebarContent>
        <SidebarSection>
          <NavItem
            to="/dashboard"
            active={isActive("/dashboard") || isActive("/")}
            collapsed={collapsed}
          >
            <Home />
            <span className="nav-text">Dashboard</span>
          </NavItem>
        </SidebarSection>

        <SidebarSection>
          <SectionTitle collapsed={collapsed}>Email Management</SectionTitle>
          <NavItem
            to="/dashboard/emails"
            active={isActive("/dashboard/emails")}
            collapsed={collapsed}
          >
            <Mail />
            <span className="nav-text">All Emails</span>
          </NavItem>
        </SidebarSection>

        <SidebarSection>
          <SectionTitle collapsed={collapsed}>Analytics & Tools</SectionTitle>
          <NavItem
            to="/dashboard/analytics"
            active={isActive("/dashboard/analytics")}
            collapsed={collapsed}
          >
            <BarChart3 />
            <span className="nav-text">Analytics</span>
          </NavItem>
          <NavItem
            to="/dashboard/settings"
            active={isActive("/dashboard/settings")}
            collapsed={collapsed}
          >
            <Settings />
            <span className="nav-text">Settings</span>
          </NavItem>
        </SidebarSection>
      </SidebarContent>

      <SidebarFooter>
        <UserSection collapsed={collapsed}>
          <UserAvatar>
            {/* --- CHANGED: Using username for initials --- */}
            {getInitials(user?.username)}
          </UserAvatar>
          <UserInfo collapsed={collapsed}>
            <div className="user-name">
              {/* --- CHANGED: Displaying username --- */}
              {user?.username}
            </div>
            <div className="user-email">
              {/* --- CHANGED: Displaying email --- */}
              {user?.email}
            </div>
          </UserInfo>
        </UserSection>

        <LogoutButton collapsed={collapsed} onClick={handleLogout}>
          <LogOut />
          <span className="logout-text">Sign Out</span>
        </LogoutButton>
      </SidebarFooter>
    </SidebarContainer>
  );
};

