import React from "react";
import styled from "styled-components";

const ConnectAccountPrompt = styled.div`
  text-align: center;
  padding: 40px;
  background: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  h3 {
    margin-bottom: 16px;
  }
  p {
    margin-bottom: 24px;
    color: ${({ theme }) => theme.colors.textSecondary};
  }
`;

const StyledButton = styled.button`
  padding: 10px 20px;
  background: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 500;
  text-decoration: none;
  display: inline-block;

  &:hover {
    background: ${({ theme }) => theme.colors.primaryHover};
  }
`;

const DashboardHome = () => {
  return (
    <ConnectAccountPrompt>
      <h3>Connect your email account</h3>
      <p>
        To start classifying emails, you need to connect your Gmail account.
      </p>
      <a
        href="http://localhost:8080/api/auth/connect/google"
        style={{ textDecoration: "none" }}
      >
        <StyledButton as="span">Connect with Google</StyledButton>
      </a>
    </ConnectAccountPrompt>
  );
};

export default DashboardHome;
