import React, {
  createContext,
  useContext,
  useReducer,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import axios from "axios";
import { toast } from "react-hot-toast";
import { jwtDecode } from "jwt-decode";

const AuthContext = createContext();

const getValidDecodedToken = (token) => {
  if (!token) return null;
  try {
    const decoded = jwtDecode(token);
    if (decoded.exp * 1000 > Date.now()) {
      return decoded;
    }
    return null;
  } catch (error) {
    return null;
  }
};

const token = localStorage.getItem("token");
const decodedToken = getValidDecodedToken(token);

const user = decodedToken
  ? { id: decodedToken.id, username: decodedToken.username }
  : null;

const initialState = {
  token: decodedToken ? token : null,
  user: user,
  isAuthenticated: !!user,
  loading: false,
  error: null,
};

const AUTH_ACTIONS = {
  REQUEST_START: "REQUEST_START",
  AUTH_SUCCESS: "AUTH_SUCCESS",
  AUTH_FAILURE: "AUTH_FAILURE",
  LOGOUT: "LOGOUT",
  CLEAR_ERRORS: "CLEAR_ERRORS",
};

const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.REQUEST_START:
      return { ...state, loading: true, error: null };
    case AUTH_ACTIONS.AUTH_SUCCESS:
      const decoded = jwtDecode(action.payload.token);
      const newUser = { id: decoded.id, username: decoded.username };
      localStorage.setItem("token", action.payload.token);
      return {
        ...state,
        token: action.payload.token,
        user: newUser,
        isAuthenticated: true,
        loading: false,
        error: null,
      };
    case AUTH_ACTIONS.AUTH_FAILURE:
    case AUTH_ACTIONS.LOGOUT:
      localStorage.removeItem("token");
      return {
        ...state,
        token: null,
        user: null,
        isAuthenticated: false,
        loading: false,
        error: action.payload,
      };
    case AUTH_ACTIONS.CLEAR_ERRORS:
      return { ...state, error: null };
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  const login = useCallback(async (email, password) => {
    dispatch({ type: AUTH_ACTIONS.REQUEST_START });
    try {
      const response = await axios.post("/api/auth/login", { email, password });
      dispatch({
        type: AUTH_ACTIONS.AUTH_SUCCESS,
        payload: { token: response.data.token },
      });
      toast.success("Logged in successfully!");
      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error || "Login failed";
      dispatch({ type: AUTH_ACTIONS.AUTH_FAILURE, payload: errorMessage });
      toast.error(errorMessage);
      return { success: false, error: errorMessage };
    }
  }, []);

  const register = useCallback(async (userData) => {
    dispatch({ type: AUTH_ACTIONS.REQUEST_START });

    try {
      const response = await axios.post("/api/auth/register", userData);

      dispatch({
        type: AUTH_ACTIONS.AUTH_SUCCESS,

        payload: { token: response.data.token },
      });

      toast.success("Account created successfully!");

      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error || "Registration failed";

      dispatch({ type: AUTH_ACTIONS.AUTH_FAILURE, payload: errorMessage });

      toast.error(errorMessage);

      return { success: false, error: errorMessage };
    }
  }, []);

  const logout = useCallback(() => {
    dispatch({ type: AUTH_ACTIONS.LOGOUT, payload: null });
    toast.success("Logged out successfully!");
  }, []);

  const clearErrors = useCallback(() => {
    dispatch({ type: AUTH_ACTIONS.CLEAR_ERRORS });
  }, []);

  useEffect(() => {
    if (state.token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${state.token}`;
    } else {
      delete axios.defaults.headers.common["Authorization"];
    }
  }, [state.token]);

  const value = useMemo(
    () => ({ ...state, login, register, logout, clearErrors }),
    [state, login, register, logout, clearErrors],
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};
