import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Routes, Route, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

import { useAuth } from "../contexts/AuthContext";
import { useSettings } from "../contexts/SettingsContext";

import Sidebar from "../components/layout/Sidebar";
import Header from "../components/layout/Header";
import LoadingSpinner from "../components/common/LoadingSpinner";
import EmailList from "./EmailList";
import EmailDetail from "./EmailDetail";
import Analytics from "./Analytics";
import Settings from "./Settings";
import ClassifyEmail from "./ClassifyEmail";

const DashboardContainer = styled.div`
  display: flex;
  min-height: 100vh;
  background: ${({ theme }) => theme.colors.background};
`;

const SidebarContainer = styled(motion.div)`
  width: ${({ theme, collapsed }) =>
    collapsed ? "0" : theme.layout.sidebar.width};
  flex-shrink: 0;
  background: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid
    ${({ theme, collapsed }) =>
      collapsed ? "transparent" : theme.colors.border};
  transition: all ${({ theme }) => theme.transitions.normal};
  z-index: ${({ theme }) => theme.zIndex.docked};
  overflow: hidden;

  @media (max-width: 768px) {
    position: fixed;
    top: 0;
    left: ${({ collapsed }) => (collapsed ? "-280px" : "0")};
    height: 100vh;
    width: 280px;
    box-shadow: ${({ theme, collapsed }) =>
      collapsed ? "none" : theme.boxShadow.xl};
    z-index: ${({ theme }) => theme.zIndex.overlay};
    border-right: 1px solid ${({ theme }) => theme.colors.border};
  }
`;

const MainContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const HeaderContainer = styled.div`
  height: ${({ theme }) => theme.layout.header.height};
  background: ${({ theme }) => theme.colors.surface};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  z-index: ${({ theme }) => theme.zIndex.sticky};
  position: sticky;
  top: 0;
`;

const ContentContainer = styled.main`
  flex: 1;
  padding: ${({ theme }) => theme.spacing[6]};
  max-width: ${({ theme }) => theme.layout.content.maxWidth};
  margin: 0 auto;
  width: 100%;
  @media (max-width: 768px) {
    padding: ${({ theme }) => theme.spacing[4]};
  }
`;

const MobileOverlay = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: ${({ theme }) => theme.zIndex.overlay - 1};
  @media (max-width: 768px) {
    display: block;
  }
`;

const Dashboard = () => {
  const { logout } = useAuth();
  const { sidebarCollapsed, toggleSidebar } = useSettings();
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();

  // Create a dummy list of 30 emails
  const [emails] = useState(() =>
    Array.from({ length: 30 }, (_, i) => ({
      id: `email${i + 1}`,
      from: `sender${i + 1}@example.com`,
      subject: `Subject for Email ${i + 1}`,
      snippet: `This is a short snippet for email number ${
        i + 1
      }. It gives a preview of the content.`,
      body: `
        <p>Hello,</p>
        <p>This is the full email body for <b>email number ${
          i + 1
        }</b>. It contains more details than the snippet and can include <strong>HTML formatting</strong>.</p>
        <p>You can add multiple paragraphs, lists, and other elements here to simulate a real email.</p>
        <ul>
          <li>Detail point 1</li>
          <li>Detail point 2</li>
        </ul>
        <p>Regards,<br/>Sender ${i + 1}</p>
      `,
      category: i % 5 === 0 ? "Invoice" : i % 3 === 0 ? "Project Update" : "Newsletter",
      date: new Date(Date.now() - i * 3600000).toISOString(), // One hour apart
    })),
  );

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const handleMobileMenuToggle = () => {
    if (isMobile) {
      setShowMobileMenu(!showMobileMenu);
    } else {
      toggleSidebar();
    }
  };

  // We are using static data, so no loading state is needed for emails.

  return (
    <DashboardContainer>
      <AnimatePresence>
        {showMobileMenu && isMobile && (
          <MobileOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowMobileMenu(false)}
          />
        )}
      </AnimatePresence>

      <SidebarContainer
        collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
      >
        <Sidebar
          collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
          onToggle={handleMobileMenuToggle}
          isMobile={isMobile}
        />
      </SidebarContainer>

      <MainContainer>
        <HeaderContainer>
          <Header onMenuToggle={handleMobileMenuToggle} showMenuButton={true} />
        </HeaderContainer>

        <ContentContainer>
          <Routes>
            <Route path="/" element={<EmailList emails={emails} />} />
            <Route path="/emails" element={<EmailList emails={emails} />} />
            <Route path="/emails/:id" element={<EmailDetail emails={emails} />} />
            <Route path="/analytics" element={<Analytics />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="/classify" element={<ClassifyEmail />} />
          </Routes>
        </ContentContainer>
      </MainContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
