const Dashboard = () => {
  const { user } = useAuth(); // Assuming useAuth provides a user object or token
  const { sidebarCollapsed, toggleSidebar } = useSettings();

  // --- REPLACED useEmail with local state ---
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [hasConnectedAccount, setHasConnectedAccount] = useState(true);

  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      if (!mobile) setShowMobileMenu(false);
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // --- REPLACED with a direct API call that includes the auth token ---
  useEffect(() => {
    const fetchDashboardData = async () => {
      setLoading(true);
      const token = localStorage.getItem("token");

      if (!token) {
        navigate("/login"); // Redirect if no token
        return;
      }

      try {
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
        const response = await axios.get("/api/emails", config);

        if (response.data.length === 0) {
          // Check if it's because no account is connected
          // This is a simplified check. A better way would be a separate endpoint /api/accounts
          const accountsResponse = await axios.get("/api/accounts", config); // Assuming this endpoint exists
          if (accountsResponse.data.length === 0) {
            setHasConnectedAccount(false);
          }
        } else {
          setHasConnectedAccount(true);
        }

        setEmails(response.data);
      } catch (error) {
        console.error("Error loading dashboard data:", error);
        if (error.response && error.response.status === 401) {
          toast.error("Your session has expired. Please log in again.");
          // Here you would call logout() from your AuthContext
          navigate("/login");
        } else {
          // This will stop the "failed to fetch" popups for other errors
          // and log them to the console instead.
          console.error("An error occurred while fetching emails.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate]);

  // --- RECALCULATED stats based on data our API actually provides ---
  const stats = useMemo(
    () => ({
      totalEmails: emails.length,
      promotions: emails.filter((email) => email.category === "Promotions")
        .length,
      spam: emails.filter((email) => email.category === "Spam").length,
      primary: emails.filter((email) => email.category === "Primary").length,
    }),
    [emails],
  );

  const handleMobileMenuToggle = () => {
    if (isMobile) {
      setShowMobileMenu(!showMobileMenu);
    } else {
      toggleSidebar();
    }
  };

  const handleConnectGmail = async () => {
    try {
      const token = localStorage.getItem("token");
      const config = { headers: { Authorization: `Bearer ${token}` } };
      // The backend will redirect, so we just change the window location
      window.location.href = "http://localhost:8080/api/connect/gmail";
    } catch (error) {
      toast.error("Could not initiate connection with Google.");
    }
  };

  if (loading) {
    return <LoadingSpinner fullHeight text="Loading your dashboard..." />;
  }

  return (
    <DashboardContainer>
      <AnimatePresence>
        {showMobileMenu && isMobile && (
          <MobileOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowMobileMenu(false)}
          />
        )}
      </AnimatePresence>

      <SidebarContainer
        collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
      >
        <Sidebar
          collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
          onToggle={handleMobileMenuToggle}
          isMobile={isMobile}
        />
      </SidebarContainer>

      <MainContainer sidebarCollapsed={isMobile ? false : sidebarCollapsed}>
        <HeaderContainer>
          <Header onMenuToggle={handleMobileMenuToggle} showMenuButton={true} />
        </HeaderContainer>

        <ContentContainer>
          {!hasConnectedAccount ? (
            <ConnectAccountPrompt>
              <h3>Connect your email account</h3>
              <p>
                To start classifying emails, you need to connect your Gmail
                account.
              </p>
              <ConnectButton onClick={handleConnectGmail}>
                Connect Gmail
              </ConnectButton>
            </ConnectAccountPrompt>
          ) : (
            <Routes>
              <Route
                path="/"
                element={
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                  >
                    <WelcomeMessage>
                      {/* --- SIMPLIFIED welcome message --- */}
                      <h2>Welcome Back!</h2>
                      <p>Here's what's happening with your emails today</p>
                    </WelcomeMessage>

                    <StatsGrid>
                      <StatCard color="primary">
                        <div className="stat-icon">📧</div>
                        <div className="stat-value">
                          {stats.totalEmails.toLocaleString()}
                        </div>
                        <div className="stat-label">Total Emails Scanned</div>
                      </StatCard>
                      <StatCard color="success">
                        <div className="stat-icon">📄</div>
                        <div className="stat-value">
                          {stats.primary.toLocaleString()}
                        </div>
                        <div className="stat-label">Primary</div>
                      </StatCard>
                      <StatCard color="info">
                        <div className="stat-icon">🎁</div>
                        <div className="stat-value">
                          {stats.promotions.toLocaleString()}
                        </div>
                        <div className="stat-label">Promotions</div>
                      </StatCard>
                      <StatCard color="error">
                        <div className="stat-icon">🛡️</div>
                        <div className="stat-value">
                          {stats.spam.toLocaleString()}
                        </div>
                        <div className="stat-label">Spam</div>
                      </StatCard>
                    </StatsGrid>
                  </motion.div>
                }
              />

              {/* Your other routes for showing email lists etc. */}
              <Route path="/emails/*" element={<EmailList emails={emails} />} />
              <Route path="/email/:id" element={<EmailDetail />} />
              <Route path="/analytics" element={<Analytics />} />
              <Route path="/settings" element={<Settings />} />
            </Routes>
          )}
        </ContentContainer>
      </MainContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
