import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { Routes, Route } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

import { useAuth } from '../contexts/AuthContext';
import { useEmail } from '../contexts/EmailContext';
import { useSettings } from '../contexts/SettingsContext';

// Layout components
import Sidebar from '../components/layout/Sidebar';
import Header from '../components/layout/Header';
import LoadingSpinner from '../components/common/LoadingSpinner';

// Page components
import DashboardHome from '../components/dashboard/DashboardHome';
import EmailList from './EmailList';
import EmailDetail from './EmailDetail';
import Analytics from './Analytics';
import Settings from './Settings';

const DashboardContainer = styled.div`
  display: flex;
  min-height: 100vh;
  background: ${({ theme }) => theme.colors.background};
  transition: background-color ${({ theme }) => theme.transitions.normal};
`;

const SidebarContainer = styled(motion.div)`
  width: ${({ theme, collapsed }) =>
    collapsed ? theme.layout.sidebar.collapsedWidth : theme.layout.sidebar.width};
  flex-shrink: 0;
  background: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid ${({ theme }) => theme.colors.border};
  transition: width ${({ theme }) => theme.transitions.normal};
  z-index: ${({ theme }) => theme.zIndex.docked};

  @media (max-width: 768px) {
    position: fixed;
    top: 0;
    left: ${({ collapsed }) => collapsed ? '-280px' : '0'};
    height: 100vh;
    width: 280px;
    box-shadow: ${({ theme, collapsed }) =>
      collapsed ? 'none' : theme.boxShadow.xl};
    z-index: ${({ theme }) => theme.zIndex.overlay};
  }
`;

const MainContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  margin-left: ${({ sidebarCollapsed, theme }) =>
    sidebarCollapsed ? theme.layout.sidebar.collapsedWidth : '0'};

  @media (max-width: 768px) {
    margin-left: 0;
  }
`;

const HeaderContainer = styled.div`
  height: ${({ theme }) => theme.layout.header.height};
  background: ${({ theme }) => theme.colors.surface};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  z-index: ${({ theme }) => theme.zIndex.sticky};
  position: sticky;
  top: 0;
`;

const ContentContainer = styled.main`
  flex: 1;
  padding: ${({ theme }) => theme.spacing[6]};
  max-width: ${({ theme }) => theme.layout.content.maxWidth};
  margin: 0 auto;
  width: 100%;

  @media (max-width: 768px) {
    padding: ${({ theme }) => theme.spacing[4]};
  }
`;

const MobileOverlay = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: ${({ theme }) => theme.zIndex.overlay - 1};

  @media (max-width: 768px) {
    display: block;
  }
`;

const WelcomeMessage = styled(motion.div)`
  background: linear-gradient(135deg,
    ${({ theme }) => theme.colors.primary}15 0%,
    ${({ theme }) => theme.colors.secondary}15 100%
  );
  border: 1px solid ${({ theme }) => theme.colors.primary}30;
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  padding: ${({ theme }) => theme.spacing[6]};
  margin-bottom: ${({ theme }) => theme.spacing[6]};
  text-align: center;

  h2 {
    color: ${({ theme }) => theme.colors.textPrimary};
    font-size: ${({ theme }) => theme.typography.fontSize.xl};
    font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
    margin-bottom: ${({ theme }) => theme.spacing[2]};
  }

  p {
    color: ${({ theme }) => theme.colors.textSecondary};
    font-size: ${({ theme }) => theme.typography.fontSize.base};
    margin: 0;
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${({ theme }) => theme.spacing[4]};
  margin-bottom: ${({ theme }) => theme.spacing[8]};
`;

const StatCard = styled(motion.div)`
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  padding: ${({ theme }) => theme.spacing[6]};
  text-align: center;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${({ theme }) => theme.boxShadow.lg};
  }

  .stat-icon {
    width: 48px;
    height: 48px;
    margin: 0 auto ${({ theme }) => theme.spacing[3]} auto;
    color: ${({ theme, color }) => theme.colors[color] || theme.colors.primary};
  }

  .stat-value {
    font-size: ${({ theme }) => theme.typography.fontSize['3xl']};
    font-weight: ${({ theme }) => theme.typography.fontWeight.bold};
    color: ${({ theme }) => theme.colors.textPrimary};
    margin-bottom: ${({ theme }) => theme.spacing[2]};
  }

  .stat-label {
    font-size: ${({ theme }) => theme.typography.fontSize.sm};
    color: ${({ theme }) => theme.colors.textSecondary};
    font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  }

  .stat-change {
    font-size: ${({ theme }) => theme.typography.fontSize.xs};
    margin-top: ${({ theme }) => theme.spacing[1]};

    &.positive {
      color: ${({ theme }) => theme.colors.success};
    }

    &.negative {
      color: ${({ theme }) => theme.colors.error};
    }
  }
`;

const QuickActions = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${({ theme }) => theme.spacing[3]};
  margin-bottom: ${({ theme }) => theme.spacing[8]};
`;

const ActionButton = styled(motion.button)`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[4]};
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  color: ${({ theme }) => theme.colors.textPrimary};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};
  text-align: left;

  &:hover {
    background: ${({ theme }) => theme.colors.surfaceHover};
    border-color: ${({ theme }) => theme.colors.primary};
    transform: translateY(-1px);
  }

  svg {
    width: 20px;
    height: 20px;
    color: ${({ theme }) => theme.colors.primary};
  }
`;

const Dashboard = () => {
  const { user } = useAuth();
  const {
    emails,
    categories,
    fetchEmails,
    fetchEmailAccounts,
    syncEmailAccounts,
    loading,
    syncing
  } = useEmail();
  const { sidebarCollapsed, toggleSidebar } = useSettings();
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth <= 768;
      setIsMobile(mobile);
      if (!mobile) {
        setShowMobileMenu(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Load initial data
  useEffect(() => {
    const loadDashboardData = async () => {
      try {
        await Promise.all([
          fetchEmails({ limit: 50 }),
          fetchEmailAccounts()
        ]);
      } catch (error) {
        console.error('Error loading dashboard data:', error);
      }
    };

    loadDashboardData();
  }, [fetchEmails, fetchEmailAccounts]);

  // Calculate statistics
  const stats = {
    totalEmails: emails.length,
    unreadEmails: emails.filter(email => !email.isRead).length,
    spamBlocked: categories.spam.count,
    accuracy: 94.5 // This would come from AI stats
  };

  const handleMobileMenuToggle = () => {
    if (isMobile) {
      setShowMobileMenu(!showMobileMenu);
    } else {
      toggleSidebar();
    }
  };

  const handleSyncEmails = async () => {
    await syncEmailAccounts();
  };

  if (loading) {
    return <LoadingSpinner fullHeight text="Loading your dashboard..." />;
  }

  return (
    <DashboardContainer>
      <AnimatePresence>
        {(showMobileMenu && isMobile) && (
          <MobileOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowMobileMenu(false)}
          />
        )}
      </AnimatePresence>

      <SidebarContainer
        collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
        initial={{ x: isMobile ? -280 : 0 }}
        animate={{ x: 0 }}
        transition={{ type: "spring", stiffness: 300, damping: 30 }}
      >
        <Sidebar
          collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
          onToggle={handleMobileMenuToggle}
          isMobile={isMobile}
        />
      </SidebarContainer>

      <MainContainer sidebarCollapsed={isMobile ? false : sidebarCollapsed}>
        <HeaderContainer>
          <Header
            onMenuToggle={handleMobileMenuToggle}
            showMenuButton={true}
          />
        </HeaderContainer>

        <ContentContainer>
          <Routes>
            <Route path="/" element={
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <WelcomeMessage
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <h2>Welcome back, {user?.firstName || 'User'}!</h2>
                  <p>Here's what's happening with your emails today</p>
                </WelcomeMessage>

                <StatsGrid>
                  <StatCard
                    color="primary"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.3 }}
                  >
                    <div className="stat-icon">📧</div>
                    <div className="stat-value">{stats.totalEmails.toLocaleString()}</div>
                    <div className="stat-label">Total Emails</div>
                    <div className="stat-change positive">+12% from last week</div>
                  </StatCard>

                  <StatCard
                    color="warning"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.4 }}
                  >
                    <div className="stat-icon">📬</div>
                    <div className="stat-value">{stats.unreadEmails.toLocaleString()}</div>
                    <div className="stat-label">Unread Emails</div>
                    <div className="stat-change negative">+3% from yesterday</div>
                  </StatCard>

                  <StatCard
                    color="error"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 }}
                  >
                    <div className="stat-icon">🛡️</div>
                    <div className="stat-value">{stats.spamBlocked.toLocaleString()}</div>
                    <div className="stat-label">Spam Blocked</div>
                    <div className="stat-change positive">-8% from last week</div>
                  </StatCard>

                  <StatCard
                    color="success"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.6 }}
                  >
                    <div className="stat-icon">🎯</div>
                    <div className="stat-value">{stats.accuracy}%</div>
                    <div className="stat-label">AI Accuracy</div>
                    <div className="stat-change positive">+2.1% improvement</div>
                  </StatCard>
                </StatsGrid>

                <QuickActions>
                  <ActionButton
                    onClick={handleSyncEmails}
                    disabled={syncing}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <span>🔄</span>
                    <div>
                      <div>{syncing ? 'Syncing...' : 'Sync Emails'}</div>
                      <div style={{ fontSize: '12px', color: '#6b7280' }}>
                        Get latest emails
                      </div>
                    </div>
                  </ActionButton>

                  <ActionButton
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <span>🤖</span>
                    <div>
                      <div>Train AI Model</div>
                      <div style={{ fontSize: '12px', color: '#6b7280' }}>
                        Improve classification
                      </div>
                    </div>
                  </ActionButton>

                  <ActionButton
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <span>📊</span>
                    <div>
                      <div>View Analytics</div>
                      <div style={{ fontSize: '12px', color: '#6b7280' }}>
                        Email insights
                      </div>
                    </div>
                  </ActionButton>

                  <ActionButton
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <span>⚙️</span>
                    <div>
                      <div>Settings</div>
                      <div style={{ fontSize: '12px', color: '#6b7280' }}>
                        Customize preferences
                      </div>
                    </div>
                  </ActionButton>
                </QuickActions>

                <DashboardHome />
              </motion.div>
            } />

            <Route path="/emails/*" element={<EmailList />} />
            <Route path="/email/:id" element={<EmailDetail />} />
            <Route path="/analytics" element={<Analytics />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </ContentContainer>
      </MainContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
