import React, { useEffect, useState, useMemo } from "react";
import styled from "styled-components";
import { Routes, Route, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import axios from "axios";
import { toast } from "react-hot-toast";

import { useAuth } from "../contexts/AuthContext";
import { useSettings } from "../contexts/SettingsContext";

// Import your page/layout components
import Sidebar from "../components/layout/Sidebar";
import Header from "../components/layout/Header";
import LoadingSpinner from "../components/common/LoadingSpinner";
import EmailList from "./EmailList";
import EmailDetail from "./EmailDetail";
import Analytics from "./Analytics";
import Settings from "./Settings";

// --- STYLED COMPONENTS ---
const DashboardContainer = styled.div`
  display: flex;
  min-height: 100vh;
  background: ${({ theme }) => theme.colors.background};
`;

const SidebarContainer = styled(motion.div)`
  width: ${({ theme, collapsed }) =>
    collapsed
      ? theme.layout.sidebar.collapsedWidth
      : theme.layout.sidebar.width};
  flex-shrink: 0;
  background: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid ${({ theme }) => theme.colors.border};
  transition: width ${({ theme }) => theme.transitions.normal};
  z-index: ${({ theme }) => theme.zIndex.docked};

  @media (max-width: 768px) {
    position: fixed;
    top: 0;
    left: ${({ collapsed }) => (collapsed ? "-280px" : "0")};
    height: 100vh;
    width: 280px;
    box-shadow: ${({ theme, collapsed }) =>
      collapsed ? "none" : theme.boxShadow.xl};
    z-index: ${({ theme }) => theme.zIndex.overlay};
  }
`;

// --- FIXED CSS HERE ---
const MainContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  /* The incorrect margin-left property has been removed to fix the layout */
`;

const HeaderContainer = styled.div`
  height: ${({ theme }) => theme.layout.header.height};
  background: ${({ theme }) => theme.colors.surface};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  z-index: ${({ theme }) => theme.zIndex.sticky};
  position: sticky;
  top: 0;
`;

const ContentContainer = styled.main`
  flex: 1;
  padding: ${({ theme }) => theme.spacing[6]};
  max-width: ${({ theme }) => theme.layout.content.maxWidth};
  margin: 0 auto;
  width: 100%;

  @media (max-width: 768px) {
    padding: ${({ theme }) => theme.spacing[4]};
  }
`;

const MobileOverlay = styled(motion.div)`
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: ${({ theme }) => theme.zIndex.overlay - 1};

  @media (max-width: 768px) {
    display: block;
  }
`;

const WelcomeMessage = styled(motion.div)`
  background: linear-gradient(
    135deg,
    ${({ theme }) => theme.colors.primary}15 0%,
    ${({ theme }) => theme.colors.secondary}15 100%
  );
  border: 1px solid ${({ theme }) => theme.colors.primary}30;
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  padding: ${({ theme }) => theme.spacing[6]};
  margin-bottom: ${({ theme }) => theme.spacing[6]};
  text-align: center;
  h2 {
    color: ${({ theme }) => theme.colors.textPrimary};
    font-size: ${({ theme }) => theme.typography.fontSize.xl};
  }
  p {
    color: ${({ theme }) => theme.colors.textSecondary};
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${({ theme }) => theme.spacing[4]};
`;

const StatCard = styled(motion.div)`
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  padding: ${({ theme }) => theme.spacing[6]};
  text-align: center;
  .stat-icon {
    font-size: 2.5rem;
    margin-bottom: 1rem;
  }
  .stat-value {
    font-size: 2.25rem;
    font-weight: 700;
  }
  .stat-label {
    font-size: 0.875rem;
    color: ${({ theme }) => theme.colors.textSecondary};
  }
`;

const ConnectAccountPrompt = styled.div`
  text-align: center;
  padding: 40px;
  background: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  h3 {
    margin-bottom: 16px;
  }
  p {
    margin-bottom: 24px;
    color: ${({ theme }) => theme.colors.textSecondary};
  }
`;

const ConnectButton = styled.button`
  padding: 10px 20px;
  background: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 500;
  &:hover {
    background: ${({ theme }) => theme.colors.primaryHover};
  }
`;

// --- MAIN COMPONENT ---
const Dashboard = () => {
  const { logout } = useAuth();
  const { sidebarCollapsed, toggleSidebar } = useSettings();

  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [hasConnectedAccount, setHasConnectedAccount] = useState(true);

  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // --- FIXED DATA FETCHING LOGIC ---
  useEffect(() => {
    const fetchDashboardData = async () => {
      setLoading(true);
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }

      try {
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const response = await axios.get("/api/emails", config);

        if (response.data.length === 0) {
          navigate("/");
        } else {
          setEmails(response.data);
        }
      } catch (error) {
        console.error("Error loading dashboard data:", error);
        if (error.response && error.response.status === 401) {
          toast.error("Your session has expired. Please log in again.");
          logout();
          navigate("/login");
        } else {
          console.error("An error occurred while fetching emails.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [navigate, logout]);

  const stats = useMemo(
    () => ({
      totalEmails: emails.length,
      promotions: emails.filter((email) => email.category === "Promotions")
        .length,
      spam: emails.filter((email) => email.category === "Spam").length,
      primary: emails.filter((email) => email.category === "Primary").length,
    }),
    [emails],
  );

  const handleMobileMenuToggle = () => {
    if (isMobile) {
      setShowMobileMenu(!showMobileMenu);
    } else {
      toggleSidebar();
    }
  };

  const handleConnectGmail = async () => {
    try {
      const token = localStorage.getItem("token");
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const response = await axios.get("/api/auth/google", config);
      window.location.href = response.data.url;
    } catch (error) {
      console.error("Error connecting to Gmail:", error);
      toast.error("Could not connect to Google. Please try again.");
    }
  };

  if (loading) {
    return <LoadingSpinner fullHeight text="Loading your dashboard..." />;
  }

  return (
    <DashboardContainer>
      <AnimatePresence>
        {showMobileMenu && isMobile && (
          <MobileOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowMobileMenu(false)}
          />
        )}
      </AnimatePresence>

      <SidebarContainer
        collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
      >
        <Sidebar
          collapsed={isMobile ? !showMobileMenu : sidebarCollapsed}
          onToggle={handleMobileMenuToggle}
          isMobile={isMobile}
        />
      </SidebarContainer>

      <MainContainer>
        <HeaderContainer>
          <Header onMenuToggle={handleMobileMenuToggle} showMenuButton={true} />
        </HeaderContainer>

        <ContentContainer>
          <Routes>
            <Route
              path="/"
              element={
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                >
                  <WelcomeMessage>
                    <h2>Welcome Back!</h2>
                    <p>Here's what's happening with your emails today</p>
                  </WelcomeMessage>
                  <StatsGrid>
                    <StatCard>
                      <div className="stat-icon">üìß</div>
                      <div className="stat-value">
                        {stats.totalEmails.toLocaleString()}
                      </div>
                      <div className="stat-label">Total Emails Scanned</div>
                    </StatCard>
                    <StatCard>
                      <div className="stat-icon">üìÑ</div>
                      <div className="stat-value">
                        {stats.primary.toLocaleString()}
                      </div>
                      <div className="stat-label">Primary</div>
                    </StatCard>
                    <StatCard>
                      <div className="stat-icon">üéÅ</div>
                      <div className="stat-value">
                        {stats.promotions.toLocaleString()}
                      </div>
                      <div className="stat-label">Promotions</div>
                    </StatCard>
                    <StatCard>
                      <div className="stat-icon">üõ°Ô∏è</div>
                      <div className="stat-value">
                        {stats.spam.toLocaleString()}
                      </div>
                      <div className="stat-label">Spam</div>
                    </StatCard>
                  </StatsGrid>
                </motion.div>
              }
            />

            <Route path="/emails/*" element={<EmailList emails={emails} />} />
            <Route path="/email/:id" element={<EmailDetail />} />
            <Route path="/analytics" element={<Analytics />} />
            <Route path="/settings" element={<Settings />} />
          </Routes>
        </ContentContainer>
      </MainContainer>
    </DashboardContainer>
  );
};

export default Dashboard;
