/* eslint-disable no-unused-vars */
import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import styled from "styled-components";
import { motion, AnimatePresence } from "framer-motion";
import {
  Home,
  Mail,
  Inbox,
  Shield,
  Gift,
  Users,
  RefreshCw,
  Star,
  BarChart3,
  Settings,
  LogOut,
  Menu,
  X,
  ChevronDown,
  ChevronRight,
  Filter,
  Archive,
  Trash2,
  AlertCircle,
} from "lucide-react";

import { useAuth } from "../../contexts/AuthContext";
import { useEmail } from "../../contexts/EmailContext";
import { useSettings } from "../../contexts/SettingsContext";

const SidebarContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid ${({ theme }) => theme.colors.border};
  width: ${({ collapsed, theme }) =>
    collapsed
      ? theme.layout.sidebar.collapsedWidth
      : theme.layout.sidebar.width};
  transition: width ${({ theme }) => theme.transitions.normal};
  overflow: hidden;
`;

const SidebarHeader = styled.div`
  padding: ${({ theme }) => theme.spacing[4]};
  border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  display: flex;
  align-items: center;
  justify-content: ${({ collapsed }) =>
    collapsed ? "center" : "space-between"};
  min-height: ${({ theme }) => theme.layout.header.height};
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  text-decoration: none;
  color: ${({ theme }) => theme.colors.textPrimary};
  font-weight: ${({ theme }) => theme.typography.fontWeight.bold};
  font-size: ${({ theme }) => theme.typography.fontSize.lg};

  .logo-icon {
    width: 32px;
    height: 32px;
    background: linear-gradient(
      135deg,
      ${({ theme }) => theme.colors.primary},
      ${({ theme }) => theme.colors.secondary}
    );
    border-radius: ${({ theme }) => theme.borderRadius.md};
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    flex-shrink: 0;
  }

  .logo-text {
    white-space: nowrap;
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
    transition: opacity ${({ theme }) => theme.transitions.fast};
  }
`;

const ToggleButton = styled.button`
  width: 32px;
  height: 32px;
  border: none;
  background: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  border-radius: ${({ theme }) => theme.borderRadius.sm};
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  svg {
    width: 20px;
    height: 20px;
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

const MobileCloseButton = styled.button`
  display: none;
  width: 32px;
  height: 32px;
  border: none;
  background: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  border-radius: ${({ theme }) => theme.borderRadius.sm};
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.gray100};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  svg {
    width: 20px;
    height: 20px;
  }

  @media (max-width: 768px) {
    display: flex;
  }
`;

const SidebarContent = styled.div`
  flex: 1;
  overflow-y: auto;
  overflow-x: hidden;
  padding: ${({ theme }) => theme.spacing[2]} 0;
`;

const SidebarSection = styled.div`
  margin-bottom: ${({ theme }) => theme.spacing[4]};
`;

const SectionTitle = styled.div`
  padding: ${({ theme }) => theme.spacing[2]} ${({ theme }) => theme.spacing[4]};
  font-size: ${({ theme }) => theme.typography.fontSize.xs};
  font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
  text-transform: uppercase;
  color: ${({ theme }) => theme.colors.textTertiary};
  letter-spacing: ${({ theme }) => theme.typography.letterSpacing.wide};
  opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
  transition: opacity ${({ theme }) => theme.transitions.fast};
`;

const NavItem = styled(Link)`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  margin: 0 ${({ theme }) => theme.spacing[2]};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  text-decoration: none;
  color: ${({ theme, active }) =>
    active ? theme.colors.primary : theme.colors.textSecondary};
  background: ${({ theme, active }) =>
    active ? theme.colors.primary + "10" : "transparent"};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme, active }) =>
    active
      ? theme.typography.fontWeight.medium
      : theme.typography.fontWeight.normal};
  transition: all ${({ theme }) => theme.transitions.fast};
  position: relative;
  justify-content: ${({ collapsed }) => (collapsed ? "center" : "flex-start")};

  &:hover {
    background: ${({ theme, active }) =>
      active ? theme.colors.primary + "20" : theme.colors.gray50};
    color: ${({ theme, active }) =>
      active ? theme.colors.primary : theme.colors.textPrimary};
  }

  svg {
    width: 20px;
    height: 20px;
    flex-shrink: 0;
  }

  .nav-text {
    white-space: nowrap;
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
    transition: opacity ${({ theme }) => theme.transitions.fast};
  }

  .nav-badge {
    margin-left: auto;
    background: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.white};
    font-size: ${({ theme }) => theme.typography.fontSize.xs};
    font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
    padding: 2px 6px;
    border-radius: ${({ theme }) => theme.borderRadius.full};
    min-width: 20px;
    text-align: center;
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
    transition: opacity ${({ theme }) => theme.transitions.fast};
  }
`;

const ExpandableNavItem = styled.div`
  margin: 0 ${({ theme }) => theme.spacing[2]};
`;

const ExpandableHeader = styled.button`
  width: 100%;
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  border: none;
  background: transparent;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  text-decoration: none;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.normal};
  transition: all ${({ theme }) => theme.transitions.fast};
  cursor: pointer;
  text-align: left;
  justify-content: ${({ collapsed }) => (collapsed ? "center" : "flex-start")};

  &:hover {
    background: ${({ theme }) => theme.colors.gray50};
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  svg {
    width: 20px;
    height: 20px;
    flex-shrink: 0;
  }

  .nav-text {
    white-space: nowrap;
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
    transition: opacity ${({ theme }) => theme.transitions.fast};
  }

  .expand-icon {
    margin-left: auto;
    transition: transform ${({ theme }) => theme.transitions.fast};
    transform: ${({ expanded }) =>
      expanded ? "rotate(90deg)" : "rotate(0deg)"};
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
  }
`;

const SubNavItems = styled(motion.div)`
  padding-left: ${({ theme }) => theme.spacing[8]};
  margin-top: ${({ theme }) => theme.spacing[1]};
`;

const SubNavItem = styled(Link)`
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[2]};
  padding: ${({ theme }) => theme.spacing[2]} ${({ theme }) => theme.spacing[3]};
  margin: ${({ theme }) => theme.spacing[1]} 0;
  border-radius: ${({ theme }) => theme.borderRadius.sm};
  text-decoration: none;
  color: ${({ theme, active }) =>
    active ? theme.colors.primary : theme.colors.textTertiary};
  background: ${({ theme, active }) =>
    active ? theme.colors.primary + "10" : "transparent"};
  font-size: ${({ theme }) => theme.typography.fontSize.xs};
  font-weight: ${({ theme, active }) =>
    active
      ? theme.typography.fontWeight.medium
      : theme.typography.fontWeight.normal};
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme, active }) =>
      active ? theme.colors.primary + "20" : theme.colors.gray50};
    color: ${({ theme, active }) =>
      active ? theme.colors.primary : theme.colors.textPrimary};
  }

  .dot {
    width: 4px;
    height: 4px;
    border-radius: 50%;
    background: currentColor;
    opacity: 0.5;
  }

  .sub-nav-badge {
    margin-left: auto;
    background: ${({ theme }) => theme.colors.gray400};
    color: ${({ theme }) => theme.colors.white};
    font-size: ${({ theme }) => theme.typography.fontSize.xs};
    padding: 1px 4px;
    border-radius: ${({ theme }) => theme.borderRadius.full};
    min-width: 16px;
    text-align: center;
  }
`;

const SidebarFooter = styled.div`
  border-top: 1px solid ${({ theme }) => theme.colors.border};
  padding: ${({ theme }) => theme.spacing[2]} 0;
`;

const UserSection = styled.div`
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  display: flex;
  align-items: center;
  gap: ${({ theme }) => theme.spacing[3]};
  justify-content: ${({ collapsed }) => (collapsed ? "center" : "flex-start")};
`;

const UserAvatar = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background: linear-gradient(
    135deg,
    ${({ theme }) => theme.colors.primary},
    ${({ theme }) => theme.colors.secondary}
  );
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  font-weight: ${({ theme }) => theme.typography.fontWeight.semibold};
  flex-shrink: 0;
`;

const UserInfo = styled.div`
  flex: 1;
  opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
  transition: opacity ${({ theme }) => theme.transitions.fast};

  .user-name {
    font-size: ${({ theme }) => theme.typography.fontSize.sm};
    font-weight: ${({ theme }) => theme.typography.fontWeight.medium};
    color: ${({ theme }) => theme.colors.textPrimary};
    white-space: nowrap;
  }

  .user-email {
    font-size: ${({ theme }) => theme.typography.fontSize.xs};
    color: ${({ theme }) => theme.colors.textSecondary};
    white-space: nowrap;
  }
`;

const LogoutButton = styled.button`
  width: ${({ collapsed }) => (collapsed ? "32px" : "100%")};
  display: flex;
  align-items: center;
  justify-content: ${({ collapsed }) => (collapsed ? "center" : "flex-start")};
  gap: ${({ theme }) => theme.spacing[3]};
  padding: ${({ theme }) => theme.spacing[3]} ${({ theme }) => theme.spacing[4]};
  margin: 0 ${({ theme }) => theme.spacing[2]};
  border: none;
  background: transparent;
  border-radius: ${({ theme }) => theme.borderRadius.md};
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: ${({ theme }) => theme.typography.fontSize.sm};
  cursor: pointer;
  transition: all ${({ theme }) => theme.transitions.fast};

  &:hover {
    background: ${({ theme }) => theme.colors.error + "10"};
    color: ${({ theme }) => theme.colors.error};
  }

  svg {
    width: 20px;
    height: 20px;
    flex-shrink: 0;
  }

  .logout-text {
    opacity: ${({ collapsed }) => (collapsed ? 0 : 1)};
    transition: opacity ${({ theme }) => theme.transitions.fast};
  }
`;

const Sidebar = ({ collapsed, onToggle, isMobile = false }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const { categories, syncing } = useEmail();
  const [expandedSections, setExpandedSections] = useState({
    categories: true,
    filters: false,
  });

  const toggleSection = (section) => {
    if (collapsed && !isMobile) return;
    setExpandedSections((prev) => ({
      ...prev,
      [section]: !prev[section],
    }));
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  const isActive = (path) => {
    if (path === "/dashboard" || path === "/") {
      return location.pathname === "/dashboard" || location.pathname === "/";
    }
    return location.pathname.startsWith(path);
  };

  const getInitials = (firstName, lastName) => {
    return `${firstName?.[0] || ""}${lastName?.[0] || ""}`.toUpperCase() || "U";
  };

  return (
    <SidebarContainer collapsed={collapsed}>
      <SidebarHeader collapsed={collapsed}>
        <Logo to="/dashboard" collapsed={collapsed}>
          <div className="logo-icon">
            <Mail size={18} />
          </div>
          <span className="logo-text">Email Classifier</span>
        </Logo>

        {isMobile ? (
          <MobileCloseButton onClick={onToggle}>
            <X />
          </MobileCloseButton>
        ) : (
          <ToggleButton onClick={onToggle}>
            <Menu />
          </ToggleButton>
        )}
      </SidebarHeader>

      <SidebarContent>
        <SidebarSection>
          <NavItem
            to="/dashboard"
            active={isActive("/dashboard") || isActive("/")}
            collapsed={collapsed}
          >
            <Home />
            <span className="nav-text">Dashboard</span>
          </NavItem>
        </SidebarSection>

        <SidebarSection>
          <SectionTitle collapsed={collapsed}>Email Management</SectionTitle>

          <NavItem
            to="/emails"
            active={isActive("/emails")}
            collapsed={collapsed}
          >
            <Mail />
            <span className="nav-text">All Emails</span>
            {!collapsed && (
              <span className="nav-badge">
                {Object.values(categories).reduce(
                  (sum, cat) => sum + cat.count,
                  0,
                )}
              </span>
            )}
          </NavItem>

          <ExpandableNavItem>
            <ExpandableHeader
              collapsed={collapsed}
              expanded={expandedSections.categories}
              onClick={() => toggleSection("categories")}
            >
              <Filter />
              <span className="nav-text">Categories</span>
              <ChevronRight className="expand-icon" size={16} />
            </ExpandableHeader>

            <AnimatePresence>
              {expandedSections.categories && !collapsed && (
                <SubNavItems
                  initial={{ height: 0, opacity: 0 }}
                  animate={{ height: "auto", opacity: 1 }}
                  exit={{ height: 0, opacity: 0 }}
                  transition={{ duration: 0.2 }}
                >
                  <SubNavItem
                    to="/emails/inbox"
                    active={location.pathname === "/emails/inbox"}
                  >
                    <div className="dot" />
                    Inbox
                    <span className="sub-nav-badge">
                      {categories.inbox.count}
                    </span>
                  </SubNavItem>

                  <SubNavItem
                    to="/emails/spam"
                    active={location.pathname === "/emails/spam"}
                  >
                    <div className="dot" />
                    Spam
                    <span className="sub-nav-badge">
                      {categories.spam.count}
                    </span>
                  </SubNavItem>

                  <SubNavItem
                    to="/emails/promotions"
                    active={location.pathname === "/emails/promotions"}
                  >
                    <div className="dot" />
                    Promotions
                    <span className="sub-nav-badge">
                      {categories.promotions.count}
                    </span>
                  </SubNavItem>

                  <SubNavItem
                    to="/emails/social"
                    active={location.pathname === "/emails/social"}
                  >
                    <div className="dot" />
                    Social
                    <span className="sub-nav-badge">
                      {categories.social.count}
                    </span>
                  </SubNavItem>

                  <SubNavItem
                    to="/emails/updates"
                    active={location.pathname === "/emails/updates"}
                  >
                    <div className="dot" />
                    Updates
                    <span className="sub-nav-badge">
                      {categories.updates.count}
                    </span>
                  </SubNavItem>

                  <SubNavItem
                    to="/emails/important"
                    active={location.pathname === "/emails/important"}
                  >
                    <div className="dot" />
                    Important
                    <span className="sub-nav-badge">
                      {categories.important.count}
                    </span>
                  </SubNavItem>
                </SubNavItems>
              )}
            </AnimatePresence>
          </ExpandableNavItem>
        </SidebarSection>

        <SidebarSection>
          <SectionTitle collapsed={collapsed}>Analytics & Tools</SectionTitle>

          <NavItem
            to="/analytics"
            active={isActive("/analytics")}
            collapsed={collapsed}
          >
            <BarChart3 />
            <span className="nav-text">Analytics</span>
          </NavItem>

          <NavItem
            to="/settings"
            active={isActive("/settings")}
            collapsed={collapsed}
          >
            <Settings />
            <span className="nav-text">Settings</span>
          </NavItem>
        </SidebarSection>
      </SidebarContent>

      <SidebarFooter>
        <UserSection collapsed={collapsed}>
          <UserAvatar>
            {getInitials(user?.firstName, user?.lastName)}
          </UserAvatar>
          <UserInfo collapsed={collapsed}>
            <div className="user-name">
              {user?.firstName} {user?.lastName}
            </div>
            <div className="user-email">{user?.email}</div>
          </UserInfo>
        </UserSection>

        <LogoutButton collapsed={collapsed} onClick={handleLogout}>
          <LogOut />
          <span className="logout-text">Sign Out</span>
        </LogoutButton>
      </SidebarFooter>
    </SidebarContainer>
  );
};

export default Sidebar;
