/* eslint-disable no-unused-vars */
import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";
import { motion, AnimatePresence } from "framer-motion";
import {
  Search, Bell, Settings, User, LogOut, Menu, Sun, Moon, ChevronDown, CheckCircle, X
} from "lucide-react";
import { useAuth } from "../../contexts/AuthContext";
import { useSettings } from "../../contexts/SettingsContext";

// Keep all your styled-components the same
// ...

const Header = ({ onMenuToggle, showMenuButton = false }) => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useSettings();
  const [searchValue, setSearchValue] = useState("");
  const [showUserMenu, setShowUserMenu] = useState(false);
  const userMenuRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (userMenuRef.current && !userMenuRef.current.contains(event.target)) {
        setShowUserMenu(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleLogout = () => {
    logout();
    setShowUserMenu(false);
  };

  const getInitials = (username) => {
    if (!username) return "U";
    return username.substring(0, 2).toUpperCase();
  };

  return (
    <HeaderContainer>
      <LeftSection>
        {showMenuButton && (
          <MenuButton onClick={onMenuToggle}>
            <Menu />
          </MenuButton>
        )}
        {/* Search bar can remain here if you want it */}
      </LeftSection>

      <RightSection>
        <IconButton onClick={toggleTheme}>
          {theme === "dark" ? <Sun /> : <Moon />}
        </IconButton>

        {/* You can add the notifications dropdown back here if you need it */}

        <DropdownContainer ref={userMenuRef}>
          <UserMenuButton
            onClick={() => setShowUserMenu(!showUserMenu)}
            className={showUserMenu ? "active" : ""}
          >
            <UserAvatar>
              {/* --- CHANGED: Using username for initials --- */}
              {getInitials(user?.username)}
            </UserAvatar>
            <UserInfo>
              <div className="user-name">
                {/* --- CHANGED: Displaying username --- */}
                {user?.username}
              </div>
              <div className="user-role">User</div>
            </UserInfo>
            <ChevronDown size={16} />
          </UserMenuButton>

          <AnimatePresence>
            {showUserMenu && (
              <DropdownMenu
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
                width="200px"
              >
                <DropdownItem onClick={() => setShowUserMenu(false)}>
                  <User />
                  Profile
                </DropdownItem>
                <DropdownItem onClick={() => setShowUserMenu(false)}>
                  <Settings />
                  Settings
                </DropdownItem>
                <DropdownItem onClick={handleLogout} className="danger">
                  <LogOut />
                  Sign Out
                </DropdownItem>
              </DropdownMenu>
            )}
          </AnimatePresence>
        </DropdownContainer>
      </RightSection>
    </HeaderContainer>
  );
};

export default Header;
