import React, {
  createContext,
  useContext,
  useReducer,
  useEffect,
  useCallback,
  useMemo,
} from "react";
import axios from "axios";
import { toast } from "react-hot-toast";
import { jwtDecode } from "jwt-decode";

// Create context
const AuthContext = createContext();

// Check if a token is valid and not expired
const isTokenValid = (token) => {
  if (!token) return false;
  try {
    const decoded = jwtDecode(token);
    // Check if the expiration time is in the future
    return decoded.exp * 1000 > Date.now();
  } catch (error) {
    // If decoding fails, the token is invalid
    return false;
  }
};

const token = localStorage.getItem("token");

// Initial state
const initialState = {
  token: token,
  isAuthenticated: isTokenValid(token),
  loading: false, // Set to false initially
  error: null,
};

// Action types
const AUTH_ACTIONS = {
  REQUEST_START: "REQUEST_START",
  AUTH_SUCCESS: "AUTH_SUCCESS",
  AUTH_FAILURE: "AUTH_FAILURE",
  LOGOUT: "LOGOUT",
  CLEAR_ERRORS: "CLEAR_ERRORS",
};

// Reducer function
const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.REQUEST_START:
      return { ...state, loading: true, error: null };
    case AUTH_ACTIONS.AUTH_SUCCESS:
      localStorage.setItem("token", action.payload.token);
      return {
        ...state,
        token: action.payload.token,
        isAuthenticated: true,
        loading: false,
        error: null,
      };
    case AUTH_ACTIONS.AUTH_FAILURE:
    case AUTH_ACTIONS.LOGOUT:
      localStorage.removeItem("token");
      return {
        ...state,
        token: null,
        isAuthenticated: false,
        loading: false,
        error: action.payload,
      };
    case AUTH_ACTIONS.CLEAR_ERRORS:
      return { ...state, error: null };
    default:
      return state;
  }
};

// Auth Provider Component
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Login function
  const login = useCallback(async (email, password) => {
    dispatch({ type: AUTH_ACTIONS.REQUEST_START });
    try {
      // NOTE: Using relative URL to work with React proxy
      const response = await axios.post("/api/auth/login", { email, password });
      dispatch({
        type: AUTH_ACTIONS.AUTH_SUCCESS,
        payload: { token: response.data.token },
      });
      toast.success("Logged in successfully!");
      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.msg || "Login failed";
      dispatch({ type: AUTH_ACTIONS.AUTH_FAILURE, payload: errorMessage });
      toast.error(errorMessage);
      return { success: false, error: errorMessage };
    }
  }, []);

  // Register function
  const register = useCallback(async (userData) => {
    dispatch({ type: AUTH_ACTIONS.REQUEST_START });
    try {
      // NOTE: Using relative URL to work with React proxy
      const response = await axios.post("/api/auth/register", userData);
      dispatch({
        type: AUTH_ACTIONS.AUTH_SUCCESS,
        payload: { token: response.data.token },
      });
      toast.success("Account created successfully!");
      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.msg || "Registration failed";
      dispatch({ type: AUTH_ACTIONS.AUTH_FAILURE, payload: errorMessage });
      toast.error(errorMessage);
      return { success: false, error: errorMessage };
    }
  }, []);

  // Logout function
  const logout = useCallback(() => {
    dispatch({ type: AUTH_ACTIONS.LOGOUT, payload: null });
    toast.success("Logged out successfully!");
  }, []);

  // Clear errors
  const clearErrors = useCallback(() => {
    dispatch({ type: AUTH_ACTIONS.CLEAR_ERRORS });
  }, []);

  // Set auth header for all subsequent axios requests
  useEffect(() => {
    if (state.token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${state.token}`;
    } else {
      delete axios.defaults.headers.common["Authorization"];
    }
  }, [state.token]);

  // Context value
  const value = useMemo(
    () => ({
      ...state,
      login,
      register,
      logout,
      clearErrors,
    }),
    [state, login, register, logout, clearErrors],
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook to use auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};
